// 从Google Sheets读取的数据
const sheetData = $('读取报告对话数据').first().json;

const dialogueID = sheetData.dialogueID;
const title = sheetData.title || '对话分析';
const tags = sheetData.tags || '';
const conversationHistory = sheetData.conversationHistory || '';

// 解析对话历史
const pairs = conversationHistory.split('----').filter(s => s.trim());
const dialogue = [];

for (const pair of pairs) {
  if (pair.startsWith('userSpeak:')) {
    dialogue.push('用户: ' + pair.replace('userSpeak:', ''));
  } else if (pair.startsWith('AISpeak:')) {
    dialogue.push('助手: ' + pair.replace('AISpeak:', ''));
  }
}

// 🎯 构建更详细的AI分析prompt
const prompt = `你是一位专业的汽车分析师和设计师，需要根据以下对话生成一份详细的车型对比报告。

【对话内容】
${dialogue.join('\n')}

【分析任务】
请深度分析对话中提到的车型，并按以下JSON格式输出详细的对比内容。

⚠️ 重要要求：
1. 每个维度必须包含【主要特点】和【详细描述】两部分
2. 详细描述要包含3-5个具体细节点
3. 即使对话中没有明确提到某个维度，也要根据车型的常识和定位进行合理推断
4. 绝对不允许出现"未提及"、"暂无数据"等内容
5. 每个描述要具体、专业、有细节

【输出格式】
\`\`\`json
{
  "vehicles": [
    {"name": "车型名称1"},
    {"name": "车型名称2"}
  ],
  "dimensions": [
    {
      "name": "外观设计",
      "车型名称1": {
        "label": "一句话总结（10字内）",
        "details": [
          "细节点1（如：贯穿式LED前大灯）",
          "细节点2（如：悬浮式车顶设计）",
          "细节点3（如：风阻系数0.28）"
        ]
      },
      "车型名称2": {
        "label": "一句话总结（10字内）",
        "details": [
          "细节点1",
          "细节点2",
          "细节点3"
        ]
      }
    }
  ]
}
\`\`\`

【必须包含的对比维度】（根据对话内容选择4-6个）
- 外观设计（如：前脸设计、车身线条、灯组造型等）
- 内饰设计（如：座椅材质、中控布局、氛围灯等）
- 空间表现（如：乘坐空间、后备箱容积、座椅布局等）
- 性能参数（如：动力系统、加速性能、续航里程等）
- 智能配置（如：自动驾驶、智能座舱、语音交互等）
- 舒适配置（如：座椅功能、空调系统、NVH等）
- 安全性能（如：主被动安全、辅助驾驶等）
- 价格定位（如：售价区间、性价比、目标人群等）

【示例输出】
\`\`\`json
{
  "vehicles": [
    {"name": "问界M7"},
    {"name": "理想L9"}
  ],
  "dimensions": [
    {
      "name": "外观设计",
      "问界M7": {
        "label": "科技简约风格",
        "details": [
          "贯穿式LED灯带设计",
          "悬浮式车顶营造科技感",
          "隐藏式门把手降低风阻",
          "19寸轮毂运动感十足"
        ]
      },
      "理想L9": {
        "label": "豪华稳重气场",
        "details": [
          "星环式LED大灯辨识度高",
          "车身尺寸5218mm彰显气势",
          "镀铬装饰提升豪华感",
          "21寸轮毂凸显旗舰定位"
        ]
      }
    },
    {
      "name": "内饰设计",
      "问界M7": {
        "label": "极简科技驾舱",
        "details": [
          "10.25寸全液晶仪表盘",
          "15.6寸中控悬浮屏",
          "HarmonyOS车机系统",
          "Nappa真皮座椅"
        ]
      },
      "理想L9": {
        "label": "豪华五屏交互",
        "details": [
          "前排双15.7寸屏幕",
          "后排双15.7寸娱乐屏",
          "HUD抬头显示",
          "7.3.4声道音响系统"
        ]
      }
    }
  ]
}
\`\`\`

现在请开始分析，直接输出JSON，不要其他内容。`;

return {
  json: {
    dialogueID: dialogueID,
    title: title,
    tags: tags,
    analysisPrompt: prompt
  }
};
